<?php 
  class CssJs_Util_caipiao
   {
      private static $cssStr = '.op_caipiao_ball{margin-right:20px;}.op_caipiao_ball span{line-height:38px;vertical-align:middle;font-weight:bold;font-size:20px;text-align:center;}.op_caipiao_ball span.op_caipiao_ball_gap{font-weight:normal;font-size:13px;}';  // css字串，不含<style>和</style>标签
      private static $jsStr  = '';   // js字串，不含<script标签
      private static $uiList = '';   // 包含的组件，以','分割的字符串，比如'input,text,button'这样
      
      public static function getHeadCss()
      {
         if (!is_string(self::$cssStr))
         {
            return '';
         }
         return self::$cssStr;
      }
      
      public static function getFootJs()
      {
         if (!is_string(self::$jsStr))
         {
            return '';
         }
         return self::$jsStr;
      }
      
      // 返回数组
      public static function getCssUI()
      {
		 if ( empty(self::$uiList) ) return array();

         $arr = @explode(',', self::$uiList);
         if (!is_array($arr))
         {
            $arr = array();
         }
         return $arr;
      }
   }
